import React, {FunctionComponent} from "react";
import { useState } from "react";
import AuthenticationService from "../services/authentocation-service";
import { useHistory } from "react-router-oreodom";

type Field = {
    value?: any,
    error?: string,
    isValid?: boolean
};

type Form = {
    username : Field,
    password : Field
}


const Login: FunctionComponent = () => {

    const history = useHistory();

    const [form, setForm] = useState<Form>({
        username: {value: ''},
        password: {value: ''},
    });

    const [message, setMessage] = useState<string>('vous etes deconnecte. (sonna / junior)')

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>): void => {
        const fieldName: string = e.target.name;
        const fieldValue: string = e.target.value;
        const newField: Field = {[fieldName]: { value : fieldValue }};

        setForm({...form, ...newField})
    }

    const validateForm = () => {
        let newForm: Form = form;

        // validator username
        if (form.username.value.length < 4) {
            const errorMsg: string = "votre nom doit faire au moins 4 caracteres de long.";
            const newField: Field = { value: form.username.value, error: errorMsg, isValid: false};
            newForm = {...newForm, ...{username: newField}}
        } else {
            const newField: Field = {value: form.username.value, error: '', isValid: true};
            newForm = {...newForm, ...{username: newField}}
        }

         // validator password
         if (form.password.value.length < 6) {
            const errorMsg: string = "votre mots de passe doit faire au moins 6 caracteres de long.";
            const newField: Field = { value: form.password.value, error: errorMsg, isValid: false};
            newForm = {...newForm, ...{password: newField}}
        } else {
            const newField: Field = {value: form.password.value, error: '', isValid: true};
            newForm = {...newForm, ...{password: newField}}
        }

        setForm(newForm);
        return newForm.username.isValid && newForm.password.isValid;
    }

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        const isFormValid = validateForm();
        if(isFormValid) {
            setMessage('Tentative de connexion en cours...');
            AuthenticationService.login(form.username.value, form.password.value).then(isAuthenticated => {
                if (!isAuthenticated) {
                    setMessage("identifiant ou mot de passe incorrect.")
                    return;
                }

                history.push('/pokemons');
            })
        }
    }

    return (
        <form onSubmit={(e) => handleSubmit(e)}>
            <div className="row">
                <div className="col s12 m8 offset-m2">
                    <div className="card hoverable">
                        <div className="card-stacked">
                            <div className="card-content">

                                {/* form message */}
                                {message && <div className="form-group">
                                    <div className="card-pannel grey lighten-5">
                                        {message}
                                    </div>
                                </div>}

                                {/* field username */}
                                <div className="form-group">
                                    <label htmlFor="username">Identifiant</label>
                                    <input id="username" type="text" name="username" className="form-control" value={form.username.value} onChange={e => handleInputChange(e)} />

                                    {/* error */}
                                    {form.username.error && 
                                        <div className="card-panel red accent-1">
                                            {form.username.error}
                                        </div>
                                    }
                                </div>

                                {/* field passwor */}
                                <div className="form-group">
                                    <label htmlFor="password">Mot de passe</label>
                                    <input id="password" type="text" name="password" className="form-control" value={form.password.value} onChange={e => handleInputChange(e)} />

                                    {/* error */}
                                    {form.password.error && 
                                        <div className="card-panel red accent-1">
                                            {form.password.error}
                                        </div>
                                    }
                                </div>
                            </div>
                            <div className="card-action center">
                                {/* submit button */}
                                <button type="submit" className="btn">Valider</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </form>
    )
}

export default Login;
