const formatType = (type: string): string => {
    let color: string;

    switch (type) {
        case "Plante":
            color ="green lighten-1"
            break;
        case "Feu" :
            color = "red lighten-1"
            break;
        case "Insecte" :
            color = "brown lighten-1"
            break;
        case "Sol" :
            color = "brown lighten-1"
            break;
        case "" :
            color = "lime accent-4";
            break;
        case "Vol" :
            color = "deep orange"
            break;
        case "Electrique" :
            color = "yellow accent-2"
            break;
        case "Combat" :
            color = "brown accent-3"
            break;

        default :
        color = "grey"    
        break;  
    }

    return `chip ${color}`;

}

export default formatType;
