const formatType = (type: string): string => {
    let color: string;

    switch (type) {
        case "Normal":
            color ="pink lighten-1"
            break;
        case "Fee" :
            color = "green lighten-1"
            break;
        case "Feu" :
            color = "red"
            break;
        case "Eau" :
            color = "blue lighten-1"
            break;
        case "Poison" :
            color = "lime accent-4";
            break;
        case "Vol" :
            color = "deep orange"
            break;
        case "Electrique" :
            color = "yellow accent-2"
            break;
        case "Combat" :
            color = "brown accent-3"
            break;

        default :
        color = "grey"    
        break;  
    }

    return `chip ${color}`;

}

export default formatType;
